-- 샘플 공지사항 데이터
INSERT OR IGNORE INTO notices (id, category, title, content, author, views, is_pinned) VALUES
(1, '공지사항', '2025년 상반기 정기 총회 안내', '구미디지털적층산업사업협동조합 2025년 상반기 정기 총회를 다음과 같이 개최합니다.\n\n일시: 2025년 2월 15일 (목) 14:00\n장소: 구미시 산호대로 253 구미첨단의료기술타워 6층\n\n의안:\n1. 2024년 사업실적 및 결산 승인의 건\n2. 2025년 사업계획 및 예산안 승인의 건\n3. 임원 선임의 건\n\n많은 참석 부탁드립니다.', '사무국', 245, TRUE),
(2, '보도자료', '구미 디지털 적층제조 산업, 혁신 기술 대상 수상', '구미디지털적층산업사업협동조합이 2024년 대한민국 제조업 혁신 기술 대상을 수상했습니다.\n\n이번 수상은 적층제조 기술을 활용한 지역 제조업 혁신과 산학연 협력 성과를 인정받은 것입니다.', '홍보팀', 512, FALSE),
(3, '행사', '3D 프린팅 기술 세미나 개최', '3D 프린팅 최신 기술 동향 및 산업 적용 사례를 공유하는 세미나를 개최합니다.\n\n일시: 2025년 3월 8일 (금) 10:00-17:00\n장소: 구미컨벤션센터\n참가비: 무료 (사전 등록 필수)\n\n주요 내용:\n- 적층제조 기술 트렌드\n- 의료/자동차 산업 적용 사례\n- 글로벌 시장 동향', '사무국', 389, TRUE);

